"use client";

import {
	Tooltip,
	TooltipContent,
	TooltipProvider,
	TooltipTrigger,
} from "@/components/ui/tooltip";
import { cn } from "@/lib/utils";
import {
	Check,
	ChevronDown,
	ChevronRight,
	ChevronUp,
	Copy,
	MoreHorizontal,
} from "lucide-react";
import * as React from "react";

type JsonViewerProps = {
	data: any;
	rootName?: string;
	defaultExpanded?: boolean;
	className?: string;
};

export default function Debug({
	data,
	rootName = "root",
	defaultExpanded = true,
	className,
}: JsonViewerProps) {
	return (
		<TooltipProvider>
			<div className={cn("font-mono text-sm ", className)}>
				<JsonNode
					name={rootName}
					data={data}
					isRoot={true}
					defaultExpanded={defaultExpanded}
				/>
			</div>
		</TooltipProvider>
	);
}

type JsonNodeProps = {
	name: string;
	data: any;
	isRoot?: boolean;
	defaultExpanded?: boolean;
	level?: number;
};

function JsonNode({
	name,
	data,
	isRoot = false,
	defaultExpanded = true,
	level = 0,
}: JsonNodeProps) {
	const [isExpanded, setIsExpanded] = React.useState(defaultExpanded);
	const [isCopied, setIsCopied] = React.useState(false);

	const handleToggle = () => {
		setIsExpanded(!isExpanded);
	};

	const copyToClipboard = (e: React.MouseEvent) => {
		e.stopPropagation();
		navigator.clipboard.writeText(JSON.stringify(data, null, 2));
		setIsCopied(true);
		setTimeout(() => setIsCopied(false), 2000);
	};

	const dataType =
		data === null ? "null" : Array.isArray(data) ? "array" : typeof data;
	const isExpandable =
		data !== null &&
		data !== undefined &&
		!(data instanceof Date) &&
		(dataType === "object" || dataType === "array");
	const itemCount =
		isExpandable && data !== null && data !== undefined
			? Object.keys(data).length
			: 0;

	return (
		<div
			className={cn("pl-4 group/object", level > 0 && "border-l border-border")}
		>
			<div
				className={cn(
					"flex items-center gap-1 py-1 hover:bg-muted/50 rounded px-1 -ml-4 cursor-pointer group/property",
					isRoot && "text-primary font-semibold",
				)}
				onClick={isExpandable ? handleToggle : undefined}
			>
				{isExpandable ? (
					<div className="flex justify-center items-center w-4 h-4">
						{isExpanded ? (
							<ChevronDown className="w-3.5 h-3.5 text-muted-foreground" />
						) : (
							<ChevronRight className="w-3.5 h-3.5 text-muted-foreground" />
						)}
					</div>
				) : (
					<div className="w-4" />
				)}

				<span className="text-primary">{name}</span>

				<span className="text-muted-foreground">
					{isExpandable ? (
						<>
							{dataType === "array" ? "[" : "{"}
							{!isExpanded && (
								<span className="text-muted-foreground">
									{" "}
									{itemCount} {itemCount === 1 ? "item" : "items"}{" "}
									{dataType === "array" ? "]" : "}"}
								</span>
							)}
						</>
					) : (
						":"
					)}
				</span>

				{!isExpandable && <JsonValue data={data} />}

				{!isExpandable && <div className="w-3.5" />}

				<button
					onClick={copyToClipboard}
					className="hover:bg-muted opacity-0 group-hover/property:opacity-100 ml-auto p-1 rounded"
					title="Copy to clipboard"
				>
					{isCopied ? (
						<Check className="w-3.5 h-3.5 text-green-500" />
					) : (
						<Copy className="w-3.5 h-3.5 text-muted-foreground" />
					)}
				</button>
			</div>

			{isExpandable && isExpanded && data !== null && data !== undefined && (
				<div className="pl-4">
					{Object.keys(data).map((key) => (
						<JsonNode
							key={key}
							name={dataType === "array" ? `${key}` : key}
							data={data[key]}
							level={level + 1}
							defaultExpanded={level < 1}
						/>
					))}
					<div className="py-1 pl-4 text-muted-foreground">
						{dataType === "array" ? "]" : "}"}
					</div>
				</div>
			)}
		</div>
	);
}

// Update the JsonValue function to make the entire row clickable with an expand icon
function JsonValue({ data }: { data: any }) {
	const [isExpanded, setIsExpanded] = React.useState(false);
	const dataType = typeof data;
	const TEXT_LIMIT = 80; // Character limit before truncation

	if (data === null) {
		return <span className="text-rose-500">null</span>;
	}

	if (data === undefined) {
		return <span className="text-muted-foreground">undefined</span>;
	}

	if (data instanceof Date) {
		return <span className="text-purple-500">{data.toISOString()}</span>;
	}

	switch (dataType) {
		case "string":
			if (data.length > TEXT_LIMIT) {
				return (
					<div
						className="group relative flex flex-1 items-center text-emerald-500 cursor-pointer"
						onClick={(e) => {
							e.stopPropagation();
							setIsExpanded(!isExpanded);
						}}
					>
						{`"`}
						{isExpanded ? (
							<span className="inline-block max-w-full">{data}</span>
						) : (
							<Tooltip delayDuration={300}>
								<TooltipTrigger asChild>
									<span className="inline-block max-w-full">
										{data.substring(0, TEXT_LIMIT)}...
									</span>
								</TooltipTrigger>
								<TooltipContent
									side="bottom"
									className="p-2 max-w-md text-xs break-words"
								>
									{data}
								</TooltipContent>
							</Tooltip>
						)}
						{`"`}
						<div className="top-1/2 right-0 absolute opacity-0 group-hover:opacity-100 transition-opacity -translate-y-1/2 translate-x-[calc(100%+4px)]">
							{isExpanded ? (
								<ChevronUp className="w-3 h-3 text-muted-foreground" />
							) : (
								<MoreHorizontal className="w-3 h-3 text-muted-foreground" />
							)}
						</div>
					</div>
				);
			}
			return <span className="text-emerald-500">{`"${data}"`}</span>;
		case "number":
			return <span className="text-amber-500">{data}</span>;
		case "boolean":
			return <span className="text-blue-500">{data.toString()}</span>;
		default:
			return <span>{String(data)}</span>;
	}
}

"use client";

import * as React from "react";
import * as TooltipPrimitive from "@radix-ui/react-tooltip";

import { cn } from "@/lib/utils";

const TooltipProvider = TooltipPrimitive.Provider;

const Tooltip = TooltipPrimitive.Root;

const TooltipTrigger = TooltipPrimitive.Trigger;

const TooltipContent = React.forwardRef<
  React.ElementRef<typeof TooltipPrimitive.Content>,
  React.ComponentPropsWithoutRef<typeof TooltipPrimitive.Content>
>(({ className, sideOffset = 4, ...props }, ref) => (
  <TooltipPrimitive.Portal>
    <TooltipPrimitive.Content
      ref={ref}
      sideOffset={sideOffset}
      className={cn(
        "z-50 overflow-hidden font-semibold rounded-md bg-primary px-3 py-1.5 text-xs text-primary-foreground animate-in fade-in-0 zoom-in-95 data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=closed]:zoom-out-95 data-[side=bottom]:slide-in-from-top-2 data-[side=left]:slide-in-from-right-2 data-[side=right]:slide-in-from-left-2 data-[side=top]:slide-in-from-bottom-2",
        className
      )}
      {...props}
    />
  </TooltipPrimitive.Portal>
));
TooltipContent.displayName = TooltipPrimitive.Content.displayName;

export { Tooltip, TooltipTrigger, TooltipContent, TooltipProvider };

import { config } from "@/config";
import { type ClassValue, clsx } from "clsx";
import { twMerge } from "tailwind-merge";

export function cn(...inputs: ClassValue[]) {
	return twMerge(clsx(inputs));
}

export function capitalize(str: string) {
	return str.charAt(0).toUpperCase() + str.slice(1);
}

// eslint-disable-next-line @typescript-eslint/no-unused-vars
type GroupBy<T, K extends keyof T> = Record<string, T[]>;

export function groupBy<T, K extends keyof T>(
	array: T[],
	key: K,
): GroupBy<T, K> {
	return array.reduce(
		(acc, item) => {
			const keyValue = String(item[key]);
			if (!acc[keyValue]) {
				acc[keyValue] = [];
			}
			acc[keyValue].push(item);
			return acc;
		},
		{} as GroupBy<T, K>,
	);
}

export function absoluteUrl(path: string) {
	return process.env.NODE_ENV === "development"
		? `http://localhost:3000${path}`
		: `https://${config.appUrl}${path}`;
}

export const config = {
  appUrl:
    process.env.NODE_ENV === "production"
      ? process.env.VERCEL_PROJECT_PRODUCTION_URL ??
        process.env.NEXT_PUBLIC_APP_URL!
      : "localhost:3000",
  social: {
    github: "https://github.com/akash3444/shadcn-ui-blocks",
    twitter: "https://twitter.com/akash_3444",
  },
};
