"use client";

import { Button } from "@/components/ui/button";
import {
	Dialog,
	DialogContent,
	DialogDescription,
	DialogFooter,
	DialogHeader,
	DialogTitle,
} from "@/components/ui/dialog";
import { cn } from "@/lib/utils";
import { CircleUserRoundIcon, CropIcon, XIcon } from "lucide-react";
import { SyntheticEvent, useCallback, useRef, useState } from "react";
import ReactCrop, {
	centerCrop,
	makeAspectCrop,
	type Crop,
	type PixelCrop,
} from "react-image-crop";
import { useFileUpload, type FileMetadata } from "./use-file-upload";

import "react-image-crop/dist/ReactCrop.css";

export interface ProfileFileUploaderProps {
	/**
	 * Size of the avatar in rem, controls the button dimensions
	 * @default 4
	 */
	size?: number;
	/**
	 * Custom class name for the root element
	 */
	className?: string;

	/**
	 * Initial file to display
	 */
	initialFile?: FileMetadata;
	/**
	 * Callback when file changes
	 */
	onFileChange?: (file: File | null) => void;
	/**
	 * Custom class name for the avatar button
	 */
	buttonClassName?: string;

	/**
	 * Whether the avatar should be rounded or square
	 * @default false
	 */
	rounded?: boolean;

	/**
	 * Enable image cropping before finalizing the upload
	 * @default false
	 */
	enableCrop?: boolean;
}

// Helper function to center the crop
function centerAspectCrop(
	mediaWidth: number,
	mediaHeight: number,
	aspect: number,
): Crop {
	return centerCrop(
		makeAspectCrop(
			{
				unit: "%",
				width: 50,
				height: 50,
			},
			aspect,
			mediaWidth,
			mediaHeight,
		),
		mediaWidth,
		mediaHeight,
	);
}

export function ProfileFileUploader({
	size = 4,
	className,
	initialFile,
	onFileChange,
	buttonClassName,
	rounded = false,
	enableCrop = false,
}: ProfileFileUploaderProps) {
	const [
		{ files, isDragging },
		{
			removeFile,
			openFileDialog,
			getInputProps,
			handleDragEnter,
			handleDragLeave,
			handleDragOver,
			handleDrop,
			addFiles,
		},
	] = useFileUpload({
		accept: "image/*",
		initialFiles: initialFile ? [initialFile] : [],
		onFilesChange: (files) => {
			if (!enableCrop) {
				onFileChange?.(files[0]?.file instanceof File ? files[0].file : null);
			}
		},
	});

	// State for handling temporary image for cropping
	const [tempImage, setTempImage] = useState<File | null>(null);
	const [tempImageUrl, setTempImageUrl] = useState<string | null>(null);

	// Crop related state
	const [dialogOpen, setDialogOpen] = useState(false);
	const [crop, setCrop] = useState<Crop>();
	const [croppedImageUrl, setCroppedImageUrl] = useState<string>("");
	const imgRef = useRef<HTMLImageElement | null>(null);

	const previewUrl = files[0]?.preview || null;
	const sizeInRem = `${size}rem`;
	const iconSizeInRem = `${size / 2}rem`;

	// Custom file handler to intercept files before adding them to useFileUpload
	const handleFileIntercept = useCallback(
		(acceptedFiles: File[]) => {
			if (enableCrop && acceptedFiles.length > 0) {
				// Store the file temporarily
				const file = acceptedFiles[0];
				setTempImage(file);

				// Create a temporary URL for the image
				const url = URL.createObjectURL(file);
				setTempImageUrl(url);

				// Open the crop dialog
				setDialogOpen(true);

				// Don't add the file to useFileUpload yet
				return;
			}

			// If cropping is disabled, add files normally
			addFiles(acceptedFiles);
		},
		[enableCrop, addFiles],
	);

	// Custom drag and drop handlers that call the interceptor
	const customHandleDrop = useCallback(
		(e: React.DragEvent<HTMLButtonElement>) => {
			handleDrop(e);

			// Get the dropped files from the event
			const droppedFiles = Array.from(e.dataTransfer.files);
			if (droppedFiles.length > 0 && enableCrop) {
				// Prevent default useFileUpload behavior by stopping event propagation
				e.stopPropagation();
				handleFileIntercept(droppedFiles);
			}
		},
		[handleDrop, handleFileIntercept, enableCrop],
	);

	// Override the default file input to use our interceptor
	const customFileInputProps = {
		...getInputProps(),
		onChange: (e: React.ChangeEvent<HTMLInputElement>) => {
			if (enableCrop && e.target.files && e.target.files.length > 0) {
				// Handle the selected files with our interceptor
				const selectedFiles = Array.from(e.target.files);
				handleFileIntercept(selectedFiles);

				// Clear the input value to allow selecting the same file again
				e.target.value = "";
			} else {
				// Use the default handler from useFileUpload
				getInputProps().onChange?.(e);
			}
		},
	};

	function onImageLoad(e: SyntheticEvent<HTMLImageElement>) {
		const { width, height } = e.currentTarget;
		setCrop(centerAspectCrop(width, height, 1));
	}

	function onCropComplete(pixelCrop: PixelCrop) {
		if (imgRef.current && pixelCrop.width && pixelCrop.height) {
			const croppedImageUrl = getCroppedImg(imgRef.current, pixelCrop);
			setCroppedImageUrl(croppedImageUrl);
		}
	}

	function getCroppedImg(image: HTMLImageElement, crop: PixelCrop): string {
		const canvas = document.createElement("canvas");
		const scaleX = image.naturalWidth / image.width;
		const scaleY = image.naturalHeight / image.height;

		canvas.width = crop.width * scaleX;
		canvas.height = crop.height * scaleY;

		const ctx = canvas.getContext("2d");

		if (ctx) {
			ctx.imageSmoothingEnabled = false;

			ctx.drawImage(
				image,
				crop.x * scaleX,
				crop.y * scaleY,
				crop.width * scaleX,
				crop.height * scaleY,
				0,
				0,
				crop.width * scaleX,
				crop.height * scaleY,
			);
		}

		return canvas.toDataURL("image/png", 1.0);
	}

	async function handleApplyCrop() {
		if (croppedImageUrl && tempImage) {
			try {
				// Convert the data URL to a Blob
				const response = await fetch(croppedImageUrl);
				const blob = await response.blob();

				// Create a new File from the Blob
				const croppedFile = new File([blob], tempImage.name, {
					type: "image/png",
					lastModified: new Date().getTime(),
				});

				// Add the cropped file to useFileUpload
				addFiles([croppedFile]);

				// Call onFileChange with the cropped file
				onFileChange?.(croppedFile);

				// Clean up temporary states
				setTempImage(null);
				if (tempImageUrl) {
					URL.revokeObjectURL(tempImageUrl);
					setTempImageUrl(null);
				}
				setCroppedImageUrl("");

				// Close the dialog
				setDialogOpen(false);
			} catch (error) {
				console.error("Error applying crop:", error);
			}
		}
	}

	function handleCancelCrop() {
		// Clean up temporary states
		setTempImage(null);
		if (tempImageUrl) {
			URL.revokeObjectURL(tempImageUrl);
			setTempImageUrl(null);
		}
		setCroppedImageUrl("");
		setDialogOpen(false);
	}

	return (
		<div className={cn("flex flex-col items-center gap-2", className)}>
			<div className="inline-flex relative">
				<Button
					variant="outline"
					className={cn(
						"relative shadow-none p-0 overflow-hidden",
						buttonClassName,
						rounded && "rounded-full border-dashed",
						isDragging && "border-primary border-dashed opacity-70",
					)}
					style={{ width: sizeInRem, height: sizeInRem }}
					onClick={openFileDialog}
					onDragEnter={handleDragEnter}
					onDragOver={handleDragOver}
					onDragLeave={handleDragLeave}
					onDrop={customHandleDrop}
					aria-label={previewUrl ? "Change image" : "Upload image"}
				>
					{previewUrl ? (
						<img
							className="size-full object-cover"
							src={previewUrl}
							alt="Preview of uploaded"
							width={size * 16}
							height={size * 16}
						/>
					) : (
						<div aria-hidden="true">
							<CircleUserRoundIcon
								className="opacity-60"
								style={{ width: iconSizeInRem, height: iconSizeInRem }}
							/>
						</div>
					)}
				</Button>
				{previewUrl && (
					<Button
						onClick={() => {
							removeFile(files[0]?.id);
							onFileChange?.(null);
						}}
						size="icon"
						className="-top-2 -right-2 absolute shadow-none border-2 border-background focus-visible:border-background rounded-full size-6"
						aria-label="Remove image"
					>
						<XIcon className="size-3.5" />
					</Button>
				)}
				<input
					{...customFileInputProps}
					className="sr-only"
					aria-label="Upload image file"
				/>
			</div>

			{enableCrop && (
				<Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
					<DialogContent className="gap-0 p-0">
						<DialogHeader>
							<DialogTitle />
							<DialogDescription />
						</DialogHeader>
						<div className="p-6 size-full">
							{tempImageUrl && (
								<ReactCrop
									crop={crop}
									onChange={(_, percentCrop) => setCrop(percentCrop)}
									onComplete={(c) => onCropComplete(c)}
									aspect={1}
									className="w-full"
								>
									<div className="size-full">
										<img
											ref={imgRef}
											className="size-full"
											alt="Image to crop"
											src={tempImageUrl}
											onLoad={onImageLoad}
										/>
									</div>
								</ReactCrop>
							)}
						</div>
						<DialogFooter className="justify-center p-6 pt-0">
							<Button
								size="sm"
								type="reset"
								className="w-fit"
								variant="outline"
								onClick={handleCancelCrop}
							>
								<XIcon className="mr-1.5 size-4" />
								Cancel
							</Button>
							<Button
								type="submit"
								size="sm"
								className="w-fit"
								onClick={handleApplyCrop}
							>
								<CropIcon className="mr-1.5 size-4" />
								Crop
							</Button>
						</DialogFooter>
					</DialogContent>
				</Dialog>
			)}
		</div>
	);
}

// Export named component as default for backward compatibility
export default ProfileFileUploader;

"use client";

import type React from "react";
import {
	type ChangeEvent,
	type DragEvent,
	type InputHTMLAttributes,
	useCallback,
	useRef,
	useState,
} from "react";

export type FileMetadata = {
	name: string;
	size: number;
	type: string;
	url: string;
	id: string;
};

export type FileWithPreview = {
	file: File | FileMetadata;
	id: string;
	preview?: string;
};

export type FileUploadOptions = {
	maxFiles?: number; // Only used when multiple is true, defaults to Infinity
	maxSize?: number; // in bytes
	accept?: string;
	multiple?: boolean; // Defaults to false
	initialFiles?: FileMetadata[];
	onFilesChange?: (files: FileWithPreview[]) => void; // Callback when files change
	onFilesAdded?: (addedFiles: FileWithPreview[]) => void; // Callback when new files are added
};

export type FileUploadState = {
	files: FileWithPreview[];
	isDragging: boolean;
	errors: string[];
};

export type FileUploadActions = {
	addFiles: (files: FileList | File[]) => void;
	removeFile: (id: string) => void;
	clearFiles: () => void;
	clearErrors: () => void;
	handleDragEnter: (e: DragEvent<HTMLElement>) => void;
	handleDragLeave: (e: DragEvent<HTMLElement>) => void;
	handleDragOver: (e: DragEvent<HTMLElement>) => void;
	handleDrop: (e: DragEvent<HTMLElement>) => void;
	handleFileChange: (e: ChangeEvent<HTMLInputElement>) => void;
	openFileDialog: () => void;
	getInputProps: (
		props?: InputHTMLAttributes<HTMLInputElement>,
	) => InputHTMLAttributes<HTMLInputElement> & {
		ref: React.Ref<HTMLInputElement>;
	};
};

export const useFileUpload = (
	options: FileUploadOptions = {},
): [FileUploadState, FileUploadActions] => {
	const {
		maxFiles = Number.POSITIVE_INFINITY,
		maxSize = Number.POSITIVE_INFINITY,
		accept = "*",
		multiple = false,
		initialFiles = [],
		onFilesChange,
		onFilesAdded,
	} = options;

	const [state, setState] = useState<FileUploadState>({
		files: initialFiles.map((file) => ({
			file,
			id: file.id,
			preview: file.url,
		})),
		isDragging: false,
		errors: [],
	});

	const inputRef = useRef<HTMLInputElement>(null);

	const validateFile = useCallback(
		(file: File | FileMetadata): string | null => {
			if (file instanceof File) {
				if (file.size > maxSize) {
					return `File "${file.name}" exceeds the maximum size of ${formatBytes(maxSize)}.`;
				}
			} else {
				if (file.size > maxSize) {
					return `File "${file.name}" exceeds the maximum size of ${formatBytes(maxSize)}.`;
				}
			}

			if (accept !== "*") {
				const acceptedTypes = accept.split(",").map((type) => type.trim());
				const fileType = file instanceof File ? file.type || "" : file.type;
				const fileExtension = `.${file instanceof File ? file.name.split(".").pop() : file.name.split(".").pop()}`;

				const isAccepted = acceptedTypes.some((type) => {
					if (type.startsWith(".")) {
						return fileExtension.toLowerCase() === type.toLowerCase();
					}
					if (type.endsWith("/*")) {
						const baseType = type.split("/")[0];
						return fileType.startsWith(`${baseType}/`);
					}
					return fileType === type;
				});

				if (!isAccepted) {
					return `File "${file instanceof File ? file.name : file.name}" is not an accepted file type.`;
				}
			}

			return null;
		},
		[accept, maxSize],
	);

	const createPreview = useCallback(
		(file: File | FileMetadata): string | undefined => {
			if (file instanceof File) {
				return URL.createObjectURL(file);
			}
			return file.url;
		},
		[],
	);

	const generateUniqueId = useCallback((file: File | FileMetadata): string => {
		if (file instanceof File) {
			return `${file.name}-${Date.now()}-${Math.random().toString(36).substring(2, 9)}`;
		}
		return file.id;
	}, []);

	const clearFiles = useCallback(() => {
		setState((prev) => {
			// Clean up object URLs
			prev.files.forEach((file) => {
				if (
					file.preview &&
					file.file instanceof File &&
					file.file.type.startsWith("image/")
				) {
					URL.revokeObjectURL(file.preview);
				}
			});

			if (inputRef.current) {
				inputRef.current.value = "";
			}

			const newState = {
				...prev,
				files: [],
				errors: [],
			};

			onFilesChange?.(newState.files);
			return newState;
		});
	}, [onFilesChange]);

	const addFiles = useCallback(
		(newFiles: FileList | File[]) => {
			if (!newFiles || newFiles.length === 0) return;

			const newFilesArray = Array.from(newFiles);
			const errors: string[] = [];

			// Clear existing errors when new files are uploaded
			setState((prev) => ({ ...prev, errors: [] }));

			// In single file mode, clear existing files first
			if (!multiple) {
				clearFiles();
			}

			// Check if adding these files would exceed maxFiles (only in multiple mode)
			if (
				multiple &&
				maxFiles !== Number.POSITIVE_INFINITY &&
				state.files.length + newFilesArray.length > maxFiles
			) {
				errors.push(`You can only upload a maximum of ${maxFiles} files.`);
				setState((prev) => ({ ...prev, errors }));
				return;
			}

			const validFiles: FileWithPreview[] = [];

			newFilesArray.forEach((file) => {
				// Check for duplicates
				const isDuplicate = state.files.some(
					(existingFile) =>
						existingFile.file.name === file.name &&
						existingFile.file.size === file.size,
				);

				// Skip duplicate files silently
				if (isDuplicate) {
					return;
				}

				// Check file size
				if (file.size > maxSize) {
					errors.push(
						multiple
							? `Some files exceed the maximum size of ${formatBytes(maxSize)}.`
							: `File exceeds the maximum size of ${formatBytes(maxSize)}.`,
					);
					return;
				}

				const error = validateFile(file);
				if (error) {
					errors.push(error);
				} else {
					validFiles.push({
						file,
						id: generateUniqueId(file),
						preview: createPreview(file),
					});
				}
			});

			// Only update state if we have valid files to add
			if (validFiles.length > 0) {
				// Call the onFilesAdded callback with the newly added valid files
				onFilesAdded?.(validFiles);

				setState((prev) => {
					const newFiles = !multiple
						? validFiles
						: [...prev.files, ...validFiles];
					onFilesChange?.(newFiles);
					return {
						...prev,
						files: newFiles,
						errors,
					};
				});
			} else if (errors.length > 0) {
				setState((prev) => ({
					...prev,
					errors,
				}));
			}

			// Reset input value after handling files
			if (inputRef.current) {
				inputRef.current.value = "";
			}
		},
		[
			state.files.length,
			maxFiles,
			multiple,
			maxSize,
			validateFile,
			createPreview,
			generateUniqueId,
			clearFiles,
			onFilesChange,
			onFilesAdded,
		],
	);

	const removeFile = useCallback(
		(id: string) => {
			setState((prev) => {
				const fileToRemove = prev.files.find((file) => file.id === id);
				if (
					fileToRemove &&
					fileToRemove.preview &&
					fileToRemove.file instanceof File &&
					fileToRemove.file.type.startsWith("image/")
				) {
					URL.revokeObjectURL(fileToRemove.preview);
				}

				const newFiles = prev.files.filter((file) => file.id !== id);
				onFilesChange?.(newFiles);

				return {
					...prev,
					files: newFiles,
					errors: [],
				};
			});
		},
		[onFilesChange],
	);

	const clearErrors = useCallback(() => {
		setState((prev) => ({
			...prev,
			errors: [],
		}));
	}, []);

	const handleDragEnter = useCallback((e: DragEvent<HTMLElement>) => {
		e.preventDefault();
		e.stopPropagation();
		setState((prev) => ({ ...prev, isDragging: true }));
	}, []);

	const handleDragLeave = useCallback((e: DragEvent<HTMLElement>) => {
		e.preventDefault();
		e.stopPropagation();

		if (e.currentTarget.contains(e.relatedTarget as Node)) {
			return;
		}

		setState((prev) => ({ ...prev, isDragging: false }));
	}, []);

	const handleDragOver = useCallback((e: DragEvent<HTMLElement>) => {
		e.preventDefault();
		e.stopPropagation();
	}, []);

	const handleDrop = useCallback(
		(e: DragEvent<HTMLElement>) => {
			e.preventDefault();
			e.stopPropagation();
			setState((prev) => ({ ...prev, isDragging: false }));

			// Don't process files if the input is disabled
			if (inputRef.current?.disabled) {
				return;
			}

			if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
				// In single file mode, only use the first file
				if (!multiple) {
					const file = e.dataTransfer.files[0];
					addFiles([file]);
				} else {
					addFiles(e.dataTransfer.files);
				}
			}
		},
		[addFiles, multiple],
	);

	const handleFileChange = useCallback(
		(e: ChangeEvent<HTMLInputElement>) => {
			if (e.target.files && e.target.files.length > 0) {
				addFiles(e.target.files);
			}
		},
		[addFiles],
	);

	const openFileDialog = useCallback(() => {
		if (inputRef.current) {
			inputRef.current.click();
		}
	}, []);

	const getInputProps = useCallback(
		(props: InputHTMLAttributes<HTMLInputElement> = {}) => {
			return {
				...props,
				type: "file" as const,
				onChange: handleFileChange,
				accept: props.accept || accept,
				multiple: props.multiple !== undefined ? props.multiple : multiple,
				ref: inputRef,
			};
		},
		[accept, multiple, handleFileChange],
	);

	return [
		state,
		{
			addFiles,
			removeFile,
			clearFiles,
			clearErrors,
			handleDragEnter,
			handleDragLeave,
			handleDragOver,
			handleDrop,
			handleFileChange,
			openFileDialog,
			getInputProps,
		},
	];
};

// Helper function to format bytes to human-readable format
export const formatBytes = (bytes: number, decimals = 2): string => {
	if (bytes === 0) return "0 Bytes";

	const k = 1024;
	const dm = decimals < 0 ? 0 : decimals;
	const sizes = ["Bytes", "KB", "MB", "GB", "TB", "PB", "EB", "ZB", "YB"];

	const i = Math.floor(Math.log(bytes) / Math.log(k));

	return Number.parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + sizes[i];
};

import * as React from "react";
import { Slot } from "@radix-ui/react-slot";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-colors focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0",
  {
    variants: {
      variant: {
        default:
          "bg-primary text-primary-foreground shadow hover:bg-primary/90",
        destructive:
          "bg-destructive text-destructive-foreground shadow-sm hover:bg-destructive/90",
        outline:
          "border border-input bg-background shadow-sm hover:bg-accent hover:text-accent-foreground",
        secondary:
          "bg-secondary text-secondary-foreground shadow-sm hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-9 px-4 py-2",
        sm: "h-8 rounded-md px-3 text-xs",
        lg: "h-10 rounded-md px-8",
        icon: "h-9 w-9",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  }
);

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean;
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button";
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    );
  }
);
Button.displayName = "Button";

export { Button, buttonVariants };

import { config } from "@/config";
import { type ClassValue, clsx } from "clsx";
import { twMerge } from "tailwind-merge";

export function cn(...inputs: ClassValue[]) {
	return twMerge(clsx(inputs));
}

export function capitalize(str: string) {
	return str.charAt(0).toUpperCase() + str.slice(1);
}

// eslint-disable-next-line @typescript-eslint/no-unused-vars
type GroupBy<T, K extends keyof T> = Record<string, T[]>;

export function groupBy<T, K extends keyof T>(
	array: T[],
	key: K,
): GroupBy<T, K> {
	return array.reduce(
		(acc, item) => {
			const keyValue = String(item[key]);
			if (!acc[keyValue]) {
				acc[keyValue] = [];
			}
			acc[keyValue].push(item);
			return acc;
		},
		{} as GroupBy<T, K>,
	);
}

export function absoluteUrl(path: string) {
	return process.env.NODE_ENV === "development"
		? `http://localhost:3000${path}`
		: `https://${config.appUrl}${path}`;
}

